export const addToCart = (pizza, quantity, varient) => (dispatch) => {
  var cartItem = {
    name: pizza.name,
    _id: pizza.id,
    image: pizza.image,
    quantity: quantity,
    varient: varient,
    prices: pizza.prices,
    price: pizza.prices[0][varient] * quantity,
  };
  dispatch({
    type: "ADD_TO_CART",
    payload: cartItem,
  });
};
