import React from "react";
import { useState } from "react";
export default function Pizzas({ pizza }) {
  const [varient, setVarients] = useState("small");
  const [quantity, setQuantity] = useState(1);
  return (
    <div
      className="shadow-lg p-3 mb-5 bg-body-tertiary rounded"
      style={{ margin: "60px" }}
    >
      <h1>{pizza.name}</h1>
      <img
        src={pizza.image}
        className="img fluid"
        style={{ height: "200px", width: "200px" }}
        alt="pizzaImage"
      />
      <div className="flex-container">
        <div className="w-100 m-1">
          <p>Varients</p>
          <select
            className="form-control"
            value={varient}
            onClick={(e) => {
              setVarients(e.target.value);
            }}
          >
            {pizza.varients.map((varient) => {
              return <option value={varient}>{varient}</option>;
            })}
          </select>
        </div>
        <div className="w-100 m-1">
          <p>Quantity</p>
          <select
            className="form-control"
            value={quantity}
            onClick={(e) => {
              setQuantity(e.target.value);
            }}
          >
            {[...Array(10).keys()].map((i) => {
              return <option value={i + 1}>{i + 1}</option>;
            })}
          </select>
        </div>
      </div>
      <div className="flex-container">
        <div className="m-2 w-100">
          <h1>Price: {pizza.prices[0][varient] * quantity}</h1>
        </div>
        <div className="m-2 w-100">
          <button className="btn btn-outline-danger">Add to card</button>
        </div>
      </div>
    </div>
  );
}
