import pizzas from "../Pizzadata";

export const getAllPizzasReducer = (state = {}, action) => {
  switch (action.type) {
    case "GET_PIZZAS_REQUESTS":
      return {
        ...state,
      };
    case "GET_PIZZA_SUCCESS":
      return {
        pizzas: action.payload,
      };
    case "GET_PIZZA_FAILED":
      return {
        pizzas: action.payload,
      };
    default:
      return state;
  }
};
